mapper.select:
		SELECT ROW_NUMBER() OVER(ORDER BY if(up_bno='0',bno,up_bno) DESC,ordernum ASC) AS no,bno,writer, btitle,
		if(DATE_FORMAT(bdate,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d'),DATE_FORMAT(bdate,'%H:%i'),DATE_FORMAT(bdate,'%Y-%m-%d')) AS bdate,depth 
  		FROM board_consulting LIMIT #{startRow},10;

		SELECT bno,writer, btitle,bcontent,up_bno,depth,ordernum,
if(DATE_FORMAT(bdate,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d'),DATE_FORMAT(bdate,'%H:%i'),DATE_FORMAT(bdate,'%Y-%m-%d')) AS bdate
  FROM board_consulting WHERE bno = #{bno};

SELECT up_bno, depth, ordernum 
FROM board_consulting
WHERE bno = #{bno}

SELECT a.coun_no,a.gr_title,a.gr_content,a.gr_apply,a.gr_oper, a.gr_del
,a.total_num,a.img,a.sex,a.major, b.name,b.major_name,b.contact  
FROM group_consulting a, members b WHERE a.coun_no = b.id AND gr_no =  #{gr_no}

SELECT gr_title,CONCAT(DATE_FORMAT(gr_oper,'%Y-%m-%d %H:%i'),' ~ ',
DATE_FORMAT(DATE_ADD(gr_oper,INTERVAL 3 HOUR),'%Y-%m-%d %H:%i')) AS gr_oper,total_num, gr_del,round
FROM group_consulting WHERE up_gr_no = #{gr_no}

WITH RECURSIVE T_TEMP_DATES AS (
   SELECT #{startDay} AS DT
UNION
   SELECT DATE_ADD(T_TEMP_DATES.DT, INTERVAL 1 DAY) FROM T_TEMP_DATES WHERE DATE_ADD(T_TEMP_DATES.DT, INTERVAL 1 DAY) <= #{endDay}
)
SELECT DT, WEEKDAY(DT) AS DAY FROM T_TEMP_DATES GROUP BY DT HAVING DAY = #{day}
